} else {
$sql = &ldquo;UPDATE &ldquo;.$wpdb-&gt;users.&rdquo; SET user_pass = &lsquo;&quot;.md5($_POST[&lsquo;emergency_pass&rsquo;]).&quot;&rsquo; WHERE User_login = &lsquo;username&rdquo;;
$link = $wpdb-&gt;query($sql);
